import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const AboutContainer = styled.section`
  padding: 6rem 0;
  background: #f8f9fa;
`;

const AboutContent = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 4rem;
  align-items: center;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    text-align: center;
  }
`;

const AboutText = styled.div`
  h2 {
    font-size: 2.5rem;
    font-weight: 700;
    color: #1c203c;
    margin-bottom: 1.5rem;
  }

  p {
    font-size: 1.1rem;
    line-height: 1.8;
    color: #4a5568;
    margin-bottom: 2rem;
  }
`;

const StatsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 2rem;
  margin-top: 2rem;
`;

const StatItem = styled.div`
  text-align: center;
  padding: 1.5rem;
  background: white;
  border-radius: 12px;
  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease;

  &:hover {
    transform: translateY(-5px);
  }

  h3 {
    font-size: 2.5rem;
    font-weight: 700;
    color: #178582;
    margin-bottom: 0.5rem;
  }

  p {
    color: #4a5568;
    font-weight: 500;
  }
`;

const AboutImage = styled.div`
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(135deg, #178582 0%, #00FFFF 100%);
    border-radius: 20px;
    opacity: 0.1;
    transform: rotate(5deg);
  }
`;

const ImagePlaceholder = styled.div`
  width: 400px;
  height: 300px;
  background: linear-gradient(135deg, #178582 0%, #00FFFF 100%);
  border-radius: 20px;
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  z-index: 1;
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 400 300"><defs><pattern id="grid" width="20" height="20" patternUnits="userSpaceOnUse"><path d="M 20 0 L 0 0 0 20" fill="none" stroke="rgba(255,255,255,0.1)" stroke-width="1"/></pattern></defs><rect width="400" height="300" fill="url(%23grid)"/><circle cx="100" cy="80" r="30" fill="rgba(255,255,255,0.2)"/><circle cx="300" cy="120" r="40" fill="rgba(255,255,255,0.15)"/><circle cx="200" cy="200" r="25" fill="rgba(255,255,255,0.1)"/><rect x="50" y="150" width="100" height="60" rx="10" fill="rgba(255,255,255,0.1)"/><rect x="250" y="180" width="80" height="50" rx="8" fill="rgba(255,255,255,0.08)"/></svg>');
    opacity: 0.8;
  }
`;

const About: React.FC = () => {
  return (
    <AboutContainer>
      <AboutContent>
        <motion.div
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8 }}
        >
          <AboutText>
            <h2>Sobre a IzataCore</h2>
            <p>
              Somos uma empresa especializada em soluções tecnológicas e automação de processos. 
              Nossa missão é transformar a forma como as empresas operam, oferecendo ferramentas 
              inteligentes que aumentam a produtividade e reduzem custos operacionais.
            </p>
            <p>
              Com anos de experiência no mercado, desenvolvemos produtos e serviços que atendem 
              às necessidades específicas de cada cliente, garantindo resultados excepcionais 
              e crescimento sustentável.
            </p>
            <StatsGrid>
              <StatItem>
                <h3>50+</h3>
                <p>Clientes Atendidos</p>
              </StatItem>
              <StatItem>
                <h3>95%</h3>
                <p>Satisfação dos Clientes</p>
              </StatItem>
              <StatItem>
                <h3>3</h3>
                <p>Produtos Principais</p>
              </StatItem>
              <StatItem>
                <h3>24/7</h3>
                <p>Suporte Técnico</p>
              </StatItem>
            </StatsGrid>
          </AboutText>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, x: 50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8, delay: 0.2 }}
        >
          <AboutImage>
            <ImagePlaceholder></ImagePlaceholder>
          </AboutImage>
        </motion.div>
      </AboutContent>
    </AboutContainer>
  );
};

export default About;
