import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const FeaturesContainer = styled.section`
  padding: 6rem 0;
  background: #f8f9fa;
`;

const FeaturesContent = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
  text-align: center;
`;

const SectionTitle = styled.h2`
  font-size: 2.5rem;
  font-weight: 700;
  color: #1c203c;
  margin-bottom: 1rem;
`;

const SectionSubtitle = styled.p`
  font-size: 1.2rem;
  color: #4a5568;
  margin-bottom: 4rem;
  max-width: 600px;
  margin-left: auto;
  margin-right: auto;
`;

const FeaturesGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
  gap: 2rem;
`;

const FeatureCard = styled(motion.div)`
  background: white;
  border-radius: 20px;
  padding: 2rem;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
  border: 1px solid #e2e8f0;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.15);
  }
`;

const FeatureIcon = styled.div`
  width: 80px;
  height: 80px;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  border-radius: 20px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 2.5rem;
  color: white;
  margin: 0 auto 1.5rem;
`;

const FeatureTitle = styled.h3`
  font-size: 1.5rem;
  font-weight: 700;
  color: #1c203c;
  margin-bottom: 1rem;
`;

const FeatureDescription = styled.p`
  color: #4a5568;
  line-height: 1.6;
  font-size: 1.1rem;
`;

const ToolFeatures: React.FC = () => {
  const features = [
    {
      icon: 'üìä',
      title: 'Dashboard Intuitivo',
      description: 'Interface moderna e f√°cil de usar que permite visualizar todos os seus ativos em um s√≥ lugar, com gr√°ficos e m√©tricas em tempo real.'
    },
    {
      icon: 'üîß',
      title: 'Gest√£o de Manuten√ß√£o',
      description: 'Automatize agendamentos de manuten√ß√£o, controle de pe√ßas e hist√≥rico de reparos para manter seus ativos sempre em perfeito funcionamento.'
    },
    {
      icon: 'üì±',
      title: 'Aplicativo Mobile',
      description: 'Acesse todas as funcionalidades do SmartAsset atrav√©s do seu smartphone ou tablet, permitindo gest√£o remota completa.'
    },
    {
      icon: 'üîî',
      title: 'Alertas Inteligentes',
      description: 'Receba notifica√ß√µes autom√°ticas sobre vencimentos, manuten√ß√µes necess√°rias e anomalias detectadas nos seus ativos.'
    },
    {
      icon: 'üìà',
      title: 'Relat√≥rios Avan√ßados',
      description: 'Gere relat√≥rios detalhados sobre performance, custos e utiliza√ß√£o dos ativos para tomada de decis√µes estrat√©gicas.'
    },
    {
      icon: 'üîó',
      title: 'Integra√ß√µes',
      description: 'Conecte-se facilmente com sistemas ERP, CMMS e outras ferramentas j√° utilizadas pela sua empresa.'
    }
  ];

  return (
    <FeaturesContainer>
      <FeaturesContent>
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
        >
          <SectionTitle>Recursos do SmartAsset</SectionTitle>
          <SectionSubtitle>
            Descubra todas as funcionalidades que tornam o SmartAsset a solu√ß√£o 
            ideal para gest√£o inteligente de ativos da sua empresa.
          </SectionSubtitle>
        </motion.div>

        <FeaturesGrid>
          {features.map((feature, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, y: 50 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: index * 0.1 }}
            >
              <FeatureCard
                whileHover={{ scale: 1.05 }}
                transition={{ duration: 0.3 }}
              >
                <FeatureIcon>{feature.icon}</FeatureIcon>
                <FeatureTitle>{feature.title}</FeatureTitle>
                <FeatureDescription>{feature.description}</FeatureDescription>
              </FeatureCard>
            </motion.div>
          ))}
        </FeaturesGrid>
      </FeaturesContent>
    </FeaturesContainer>
  );
};

export default ToolFeatures;
